// Parse KSC Event Notification → kscEvent JSON
// Вход: item.json.textPlain (сырое письмо KSC)
// Выход: item.json = kscEvent

function parseKscNotification(raw) {
  if (!raw) return null;

  const text = String(raw).replace(/\r\n/g, '\n');

  function getField(label) {
    const re = new RegExp('^' + label + '\\s*:\\s*(.*)$', 'mi');
    const m = text.match(re);
    return m ? m[1].trim() : null;
  }

  function getDescription() {
    const marker = 'Description:';
    const idx = text.indexOf(marker);
    if (idx === -1) return null;
    return text.slice(idx + marker.length).trim();
  }

  return {
    taskName:     getField('TaskName'),
    event:        getField('Event'),
    riseTime:     getField('RiseTime'),
    severity:     getField('Severity'),
    computer:     getField('Computer'),
    domain:       getField('Domain'),
    hostIp:       getField('HostIP'),
    connectionIp: getField('ConnectionIP'),
    product:      getField('Product'),
    version:      getField('Version'),
    description:  getDescription(),
    raw:          text,
  };
}

return $input.all().map(item => {
  const body = item.json.textPlain || "";
  const kscEvent = parseKscNotification(body) || {};
  return { json: kscEvent };
});

// Normalize field names: camelCase → snake_case (рекурсивно по вложенным объектам)

function camelToSnake(str) {
  return str.replace(/([A-Z])/g, '_$1').toLowerCase();
}

function transformObject(obj) {
  const out = {};
  for (const [key, value] of Object.entries(obj)) {
    const newKey = camelToSnake(key);
    if (value && typeof value === 'object' && !Array.isArray(value)) {
      out[newKey] = transformObject(value);
    } else {
      out[newKey] = value;
    }
  }
  return out;
}

return $input.all().map(item => {
  return {
    json: transformObject(item.json),
  };
});

const uuid = require('uuid');

// ======================
// Константы
// ======================

const MONTHS_RU = {
  января: "01", февраля: "02", марта: "03", апреля: "04",
  мая: "05", июня: "06", июля: "07", августа: "08",
  сентября: "09", октября: "10", ноября: "12", декабря: "12"
};

// Исправь, если у тебя правильно: здесь я по памяти, можешь вернуть твой объект MONTHS_RU

const SEVERITY_MAP = {
  "критическое": "Critical",
  "высокое": "High",
  "среднее": "Medium",
  "низкое": "Low",
  "информационное": "Info",

  "крит": "Critical",
  "выс": "High",
  "сред": "Medium",
  "низ": "Low",
  "инфо": "Info",
};

// ======================
// Хелперы
// ======================

function normalizeSeverity(sev) {
  if (!sev) return sev;
  const cleaned = String(sev).trim().toLowerCase();
  return SEVERITY_MAP[cleaned] || sev;
}

function parseKaspeDate(str) {
  if (!str) return null;

  const cleaned = str.replace(" г.", "");

  const match = cleaned.match(
    /(\d{1,2})\s+([А-Яа-я]+)\s+(\d{4})\s+(\d{1,2}:\d{2}:\d{2})\s+\(GMT([+-]\d{1,2}):?(\d{2})?\)/
  );

  if (!match) return null;

  const [, day, monthName, year, time, tzHour, tzMin = "00"] = match;
  const month = MONTHS_RU[monthName.toLowerCase()];
  if (!month) return null;

  const iso = `${year}-${month}-${day.padStart(2, "0")}T${time}${tzHour}:${tzMin}`;
  return DateTime.fromISO(iso);
}

// ======================
// Основная логика
// ======================

const items = $input.all();

for (const item of items) {
  const json = item.json;

  // Базовые служебные поля
  json.status = "ingested";
  json.updated_at = DateTime.now().toISO();
  json.uuid = uuid.v4();

  // Нормализуем дату/время
  const parsedDate = parseKaspeDate(json.rise_time);
  json.rise_time = parsedDate ? parsedDate.toISO() : (json.rise_time || null);

  // Severity
  json.severity = normalizeSeverity(json.severity);
}

return items;

const CryptoJS = require('crypto-js');

// ======================
// Маппинг ключей description → канонические поля
// ======================

const KEY_MAP = {
  // Базовые
  "event type": "event_type",
  "тип события": "event_type",

  "name": "name",
  "имя": "name",
  "object": "object",
  "объект": "object",

  "application": "application",
  "приложение": "application",
  "application path": "application_path",
  "путь к приложению": "application_path",

  "process id": "process_id",
  "идентификатор процесса": "process_id",
  "process": "process_name",
  "process name": "process_name",

  "user": "user",
  "пользователь": "user",
  "initiator": "user",
  "инициатор": "user",

  "component": "component",
  "компонент": "component",

  "task name": "task_name",
  "задание": "task_name",

  "object name": "object_name",
  "имя объекта": "object_name",
  "object type": "object_type",
  "тип объекта": "object_type",
  "path to object": "path_to_object",
  "путь к объекту": "path_to_object",

  "md5": "object_hash_md5",
  "sha256": "object_hash_sha256",

  "result": "result_description",
  "result description": "result_description",
  "описание результата": "result_description",

  "reason": "reason",
  "причина": "reason",

  "precision": "precision",
  "точность": "precision",

  "protocol": "protocol",
  "protocol version": "protocol_version",

  "sender": "sender",
  "receiver": "receiver",

  "attackingcomputermacaddress": "attacking_mac",
  "attacking computer mac address": "attacking_mac",

  "database release date": "database_release_date",
  "дата выпуска баз": "database_release_date",

  "threat level": "threat_level",
  "степень угрозы": "threat_level",

  "type": "type",
  "тип": "type",

  "additional": "additional",
  "дополнительно": "additional"
};

// ======================
// Хелперы для description
// ======================

function normalizeKey(rawKey) {
  if (!rawKey) return null;
  let key = String(rawKey).trim();

  // отрезаем текст в скобках: "Name (application)" → "Name"
  key = key.replace(/\s*\(.*\)\s*$/, "");

  const lowered = key.toLowerCase();

  // Иногда KES пишет странно слепленные слова (AttackingComputerMACAddress)
  const compact = lowered.replace(/\s+/g, "");

  // 1) пробуем по нормальному lower-case
  if (KEY_MAP[lowered]) return KEY_MAP[lowered];

  // 2) пробуем по "слепленному"
  if (KEY_MAP[compact]) return KEY_MAP[compact];

  // 3) если нет в словаре — вернем generic-версии (snake_case),
  //   но ТОЛЬКО латиницу, остальное вычистим
  const slug = lowered
    .replace(/[^\sa-z0-9]/g, " ")
    .trim()
    .replace(/\s+/g, "_");

  return slug || null;
}

function addValue(result, key, value) {
  if (value == null || value === "") return;

  value = String(value).trim();

  // Особый кейс для name → массив
  if (key === "name") {
    if (result.name == null) {
      result.name = value;
    } else if (Array.isArray(result.name)) {
      result.name.push(value);
    } else {
      result.name = [result.name, value];
    }
    return;
  }

  // Для остальных: если ключ повторяется — превращаем в массив
  if (result[key] == null) {
    result[key] = value;
  } else if (Array.isArray(result[key])) {
    result[key].push(value);
  } else {
    result[key] = [result[key], value];
  }
}

function parseDescriptionText(descText) {
  const result = {};
  if (!descText) return result;
  result.raw = descText;

  const text = String(descText).replace(/\r\n/g, "\n");
  const lines = text.split("\n");

  let currentCanonKey = null;

  for (let rawLine of lines) {
    if (!rawLine) continue;

    const line = rawLine.trim();

    const colonIndex = line.indexOf(":");
    if (colonIndex > 0) {
      currentCanonKey = null;

      const rawKey = line.slice(0, colonIndex);
      const rawValue = line.slice(colonIndex + 1);

      const canonKey = normalizeKey(rawKey);
      if (!canonKey) continue;

      currentCanonKey = canonKey;
      addValue(result, canonKey, rawValue);
    } else {
      if (currentCanonKey && result[currentCanonKey] != null) {
        if (Array.isArray(result[currentCanonKey])) {
          const lastIndex = result[currentCanonKey].length - 1;
          result[currentCanonKey][lastIndex] =
            String(result[currentCanonKey][lastIndex]) + "\n" + line;
        } else {
          result[currentCanonKey] =
            String(result[currentCanonKey]) + "\n" + line;
        }
      }
    }
  }

  return result;
}

// ======================
// Хелпер для хэша события
// ======================

function generateEventHash(json) {
  let riseTimeStr = "";

  if (json.rise_time) {
    if (typeof json.rise_time === "string") {
      riseTimeStr = json.rise_time;
    } else if (typeof json.rise_time.toString === "function") {
      riseTimeStr = json.rise_time.toString();
    }
  }

  const payload = {
    event:     json.event || "",
    computer:  json.computer || "",
    ip:        json.host_ip || "",
    rise_time: riseTimeStr.split(".")[0] || "",
    raw:       json.raw || "",
  };

  return CryptoJS.SHA256(JSON.stringify(payload)).toString();
}

// ======================
// Основная логика
// ======================

const items = $input.all();

for (const item of items) {
  const json = item.json;

  json.description = parseDescriptionText(json.description);
  json.hash = generateEventHash(json);
}

return items;
