const items = $input.all();

for (const item of items) {
  const json = item.json;

  // Базовые служебные поля
  json.status = "ingested";
  json.updated_at = DateTime.now().toISO();
  json.uuid = uuid.v4();

  // Нормализуем дату/время
  const parsedDate = parseKaspeDate(json.rise_time);
  json.rise_time = parsedDate ? parsedDate.toISO() : (json.rise_time || null);

  // Severity
  json.severity = normalizeSeverity(json.severity);
}

return items;

const CryptoJS = require('crypto-js');

// ======================
// Маппинг ключей description → канонические поля
// ======================

const KEY_MAP = {
  // Базовые
  "event type": "event_type",
  "тип события": "event_type",

  "name": "name",
  "имя": "name",
  "object": "object",
  "объект": "object",

  "application": "application",
  "приложение": "application",
  "application path": "application_path",
  "путь к приложению": "application_path",

  "process id": "process_id",
  "идентификатор процесса": "process_id",
  "process": "process_name",
  "process name": "process_name",

  "user": "user",
  "пользователь": "user",
  "initiator": "user",
  "инициатор": "user",

  "component": "component",
  "компонент": "component",

  "task name": "task_name",
  "задание": "task_name",

  "object name": "object_name",
  "имя объекта": "object_name",
  "object type": "object_type",
  "тип объекта": "object_type",
  "path to object": "path_to_object",
  "путь к объекту": "path_to_object",

  "md5": "object_hash_md5",
  "sha256": "object_hash_sha256",

  "result": "result_description",
  "result description": "result_description",
  "описание результата": "result_description",

  "reason": "reason",
  "причина": "reason",

  "precision": "precision",
  "точность": "precision",

  "protocol": "protocol",
  "protocol version": "protocol_version",

  "sender": "sender",
  "receiver": "receiver",

  "attackingcomputermacaddress": "attacking_mac",
  "attacking computer mac address": "attacking_mac",

  "database release date": "database_release_date",
  "дата выпуска баз": "database_release_date",

  "threat level": "threat_level",
  "степень угрозы": "threat_level",

  "type": "type",
  "тип": "type",

  "additional": "additional",
  "дополнительно": "additional"
};

// ======================
// Хелперы для description
// ======================

function normalizeKey(rawKey) {
  if (!rawKey) return null;
  let key = String(rawKey).trim();

  // отрезаем текст в скобках: "Name (application)" → "Name"
  key = key.replace(/\s*\(.*\)\s*$/, "");

  const lowered = key.toLowerCase();

  // Иногда KES пишет странно слепленные слова (AttackingComputerMACAddress)
  const compact = lowered.replace(/\s+/g, "");

  // 1) пробуем по нормальному lower-case
  if (KEY_MAP[lowered]) return KEY_MAP[lowered];

  // 2) пробуем по "слепленному"
  if (KEY_MAP[compact]) return KEY_MAP[compact];

  // 3) если нет в словаре — вернем generic-версии (snake_case),
  //   но ТОЛЬКО латиницу, остальное вычистим
  const slug = lowered
    .replace(/[^\sa-z0-9]/g, " ")
    .trim()
    .replace(/\s+/g, "_");

  return slug || null;
}

function addValue(result, key, value) {
  if (value == null || value === "") return;

  value = String(value).trim();

  // Особый кейс для name → массив
  if (key === "name") {
    if (result.name == null) {
      result.name = value;
    } else if (Array.isArray(result.name)) {
      result.name.push(value);
    } else {
      result.name = [result.name, value];
    }
    return;
  }

  // Для остальных: если ключ повторяется — превращаем в массив
  if (result[key] == null) {
    result[key] = value;
  } else if (Array.isArray(result[key])) {
    result[key].push(value);
  } else {
    result[key] = [result[key], value];
  }
}

function parseDescriptionText(descText) {
  const result = {};
  if (!descText) return result;
  result.raw = descText;

  const text = String(descText).replace(/\r\n/g, "\n");
  const lines = text.split("\n");

  let currentCanonKey = null;

  for (let rawLine of lines) {
    if (!rawLine) continue;

    const line = rawLine.trim();

    const colonIndex = line.indexOf(":");
    if (colonIndex > 0) {
      currentCanonKey = null;

      const rawKey = line.slice(0, colonIndex);
      const rawValue = line.slice(colonIndex + 1);

      const canonKey = normalizeKey(rawKey);
      if (!canonKey) continue;

      currentCanonKey = canonKey;
      addValue(result, canonKey, rawValue);
    } else {
      if (currentCanonKey && result[currentCanonKey] != null) {
        if (Array.isArray(result[currentCanonKey])) {
          const lastIndex = result[currentCanonKey].length - 1;
          result[currentCanonKey][lastIndex] =
            String(result[currentCanonKey][lastIndex]) + "\n" + line;
        } else {
          result[currentCanonKey] =
            String(result[currentCanonKey]) + "\n" + line;
        }
      }
    }
  }

  return result;
}

// ======================
// Хелпер для хэша события
// ======================

function generateEventHash(json) {
  let riseTimeStr = "";

  if (json.rise_time) {
    if (typeof json.rise_time === "string") {
      riseTimeStr = json.rise_time;
    } else if (typeof json.rise_time.toString === "function") {
      riseTimeStr = json.rise_time.toString();
    }
  }

  const payload = {
    event:     json.event || "",
    computer:  json.computer || "",
    ip:        json.host_ip || "",
    rise_time: riseTimeStr.split(".")[0] || "",
    raw:       json.raw || "",
  };

  return CryptoJS.SHA256(JSON.stringify(payload)).toString();
}

// ======================
// Основная логика
// ======================

const items = $input.all();

for (const item of items) {
  const json = item.json;

  json.description = parseDescriptionText(json.description);
  json.hash = generateEventHash(json);
}

return items;

const CryptoJS = require('crypto-js');

// --- Хелперы ---

function addIfPresent(target, key, value) {
  if (value === undefined || value === null) return;
  if (typeof value === 'string' && value.trim() === '') return;
  target[key] = value;
}

function normalizeUrl(url) {
  if (!url || typeof url !== 'string') return url;
  let cleaned = url.split('#')[0].split('?')[0];
  cleaned = cleaned.replace(/\/+$/, '');
  return cleaned;
}

/**
 * Извлекаем календарную дату (YYYY-MM-DD) из rise_time
 * для учёта в incident_key (уникальность в пределах 24 часов).
 *
 * Ожидается ISO-строка вида "2025-11-27T11:40:35.000+03:00".
 * При ошибке парсинга вернёт null.
 */
function extractRiseDate(ev) {
  const rt = ev.rise_time || ev.riseTime;
  if (!rt) return null;

  try {
    const d = new Date(rt);
    if (Number.isNaN(d.getTime())) return null;

    // Берём UTC-дату (стабильно для всех нод).
    // Если нужно строго по локальному времени KSC – можно заменить на ручной парсинг.
    return d.toISOString().slice(0, 10); // 'YYYY-MM-DD'
  } catch (e) {
    return null;
  }
}

// --- Константы с реальными значениями из KSC ---
// (всё в lowercase для удобства сравнения)
//
// Названия компонентов и событий берём из официальной
// терминологии Kaspersky Endpoint Security:
// - Web Threat Protection / Защита от веб-угроз
// - Network Threat Protection / Защита от сетевых атак / Защита от сетевых угроз
// - File Threat Protection / Защита от файловых угроз
// - Event: Visit to a website was prevented / Остановлен переход на сайт
// - Event: Network attack detected / Обнаружена сетевая атака
// - Event: Infected or other object detected / Обнаружен зараженный или подозрительный объект
// - Event: Malicious object detected / Обнаружен вредоносный объект

const WEB_COMBOS = [
  // EN: Web Threat Protection + Visit to a website was prevented
  {
    task: 'web threat protection',
    event: 'visit to a website was prevented',
  },
  // RU: Защита от веб-угроз + Остановлен переход на сайт
  {
    task: 'защита от веб-угроз',
    event: 'остановлен переход на сайт',
  },
  // Возможный официальный вариант формулировки
  {
    task: 'защита от веб-угроз',
    event: 'переход на веб-сайт заблокирован',
  },
];

const NET_ATTACK_COMBOS = [
  // EN: Network Threat Protection + Network attack detected
  {
    task: 'network threat protection',
    event: 'network attack detected',
  },
  // RU: Защита от сетевых атак + Обнаружена сетевая атака
  {
    task: 'защита от сетевых атак',
    event: 'обнаружена сетевая атака',
  },
  // В некоторых логах компонент пишется как "Защита от сетевых угроз"
  {
    task: 'защита от сетевых угроз',
    event: 'обнаружена сетевая атака',
  },
];

const MALWARE_COMBOS = [
  // EN: Infected or other object detected (часто в KSC для File / Network объектов)
  {
    task: 'network threat protection',
    event: 'infected or other object detected',
  },
  // RU: Обнаружен зараженный или подозрительный объект
  {
    task: 'защита от файловых угроз',
    event: 'обнаружен зараженный или подозрительный объект',
  },
  {
    task: 'file threat protection',
    event: 'infected or other object detected',
  },

  // EN: Malicious object detected (типовое событие File Threat Protection)
  {
    task: 'file threat protection',
    event: 'malicious object detected',
  },
  {
    task: 'scheduled kes scan, full - weekly, all devices',
    event: 'malicious object detected',
  },
  // RU: Обнаружен вредоносный объект (локальные базы)
  {
    task: 'защита от файловых угроз',
    event: 'обнаружен вредоносный объект',
  },
];

// true, если (task_name,event) входит в список комбинаций
function matchesCombo(taskName, eventType, combos) {
  const t = (taskName || '').toLowerCase();
  const e = (eventType || '').toLowerCase();
  return combos.some((c) => t === c.task && e === c.event);
}

/**
 * normalizedEvent — объект по твоей JSON-схеме
 * + желательно иметь:
 *   - task_name (из KSC TaskName / Component)
 *   - event (оригинальный текст Event)
 */
function buildIncidentCore(ev) {
  const core = {};

  const deviceName =
    ev.device_name || ev.computer || ev.host;
  const componentRaw =
    ev.component || ev.module || ev.task_name || '';
  const eventRaw =
    ev.event_type || ev.event || '';

  addIfPresent(core, 'device_name', deviceName);
  addIfPresent(core, 'component', componentRaw);
  addIfPresent(core, 'event_type', eventRaw);

  const taskName = (ev.task_name || componentRaw || '').toLowerCase();
  const eventType = (ev.event || ev.event_type || '').toLowerCase();

  // === 1. WEB (Web Threat Protection / Защита от веб-угроз) ===
  if (matchesCombo(taskName, eventType, WEB_COMBOS)) {
    // Нормализованный URL
    const urlNorm =
      normalizeUrl(ev.url) ||
      normalizeUrl(ev.path_to_object);

    addIfPresent(core, 'url', urlNorm);
    addIfPresent(core, 'domain_name', ev.domain_name);
    addIfPresent(
      core,
      'threat_name',
      ev.threat_name || ev.object_detected || ev.name,
    );
    addIfPresent(core, 'threat_type', ev.threat_type);
    // addIfPresent(core, 'user', ev.user); // если нужно делать инцидент по пользователю

    return core;
  }

  // === 2. NETWORK ATTACK (Network Threat Protection / Защита от сетевых атак) ===
  if (matchesCombo(taskName, eventType, NET_ATTACK_COMBOS)) {
    addIfPresent(
      core,
      'threat_name',
      ev.threat_name || ev.object_detected || ev.name,
    );

    // Жертва (как в RDP-бруте 93–95: 5.228.4.95:3389)
    addIfPresent(core, 'target_ip', ev.target_ip);
    addIfPresent(core, 'target_port', ev.target_port);

    addIfPresent(core, 'protocol', ev.protocol);

    // Если нужно учитывать источник — можно добавить:
    // addIfPresent(core, 'attacking_ip', ev.attacking_ip);

    return core;
  }

  // === 3. MALWARE / FILE (Malicious object detected / Infected or other object detected) ===
  if (matchesCombo(taskName, eventType, MALWARE_COMBOS)) {
    const objectHash =
      ev.object_hash_sha256 ||
      ev.object_hash_md5 ||
      ev.process_sha256 ||
      ev.process_md5;

    addIfPresent(core, 'object_hash', objectHash);
    addIfPresent(
      core,
      'threat_name',
      ev.threat_name || ev.object_detected || ev.name,
    );
    addIfPresent(
      core,
      'path_to_object',
      ev.path_to_object || ev.object || ev.object_name,
    );

    return core;
  }

  // === 4. Fallback для экзотики / новых типов ===
  addIfPresent(core, 'name', ev.name);
  addIfPresent(
    core,
    'threat_name',
    ev.threat_name || ev.object_detected,
  );
  addIfPresent(core, 'object_type', ev.object_type);

  return core;
}

/**
 * incident_key = SHA256( incident_core + rise_date )
 *
 * rise_date берём из ev.rise_time как YYYY-MM-DD (UTC).
 * Это делает ключ уникальным в пределах 24 часов
 * для одной и той же комбинации полей core.
 */
function buildIncidentKey(ev) {
  const core = buildIncidentCore(ev);

  const riseDate = extractRiseDate(ev);
  if (riseDate) {
    // Техническое поле, чтобы явно "подмешать" дату в хеш.
    // В БД можно его не сохранять, но для дебага удобно.
    core._rise_date = riseDate;
  }

  const coreJson = JSON.stringify(core);
  return CryptoJS.SHA256(coreJson).toString();
}

// --- Основной цикл ноды ---

for (const item of $input.all()) {
  const ev = item.json; // или item.json.normalized — как у тебя заведено
  item.json.incident_key = buildIncidentKey(ev);
  item.json.call_counter = 0;
}

return $input.all();
