// Function node (vanilla JS)
// Парсит Event Notification от KSC в JSON-объект kscEvent

function parseKscNotification(raw) {
  if (!raw) return null;

  // Нормализуем перевод строк
  const text = String(raw).replace(/\r\n/g, '\n');

  // Утилита для вытаскивания значения по "Ключ: значение"
  function getField(label) {
    // Пример: label = "TaskName" → ищем строку "TaskName: ..."
    const re = new RegExp('^' + label + '\\s*:\\s*(.*)$', 'mi');
    const m = text.match(re);
    return m ? m[1].trim() : null;
  }

  // Description — всё после строки "Description:"
  function getDescription() {
    const marker = 'Description:';
    const idx = text.indexOf(marker);
    if (idx === -1) return null;

    // Всё, что после "Description:" до конца письма
    const after = text.slice(idx + marker.length);
    return after.trim();
  }

  return {
    taskName:      getField('TaskName'),     // %KLCSAK_EVENT_TASK_DISPLAY_NAME%
    event:         getField('Event'),        // %EVENT%
    riseTime:      getField('RiseTime'),     // %RISE_TIME%
    severity:      getField('Severity'),     // %SEVERITY%
    computer:      getField('Computer'),     // %COMPUTER%
    domain:        getField('Domain'),       // %DOMAIN%
    hostIp:        getField('HostIP'),       // %HOST_IP%
    connectionIp:  getField('ConnectionIP'), // %HOST_CONN_IP%
    product:       getField('Product'),      // %KL_PRODUCT%
    version:       getField('Version'),      // %KL_VERSION%
    description:   getDescription(),         // %DESCR% (полный блок, без парсинга)
    raw:           text                      // на всякий случай сохраняем исходник
  };
}

return items.map(item => {
  const body = item.json.textPlain || "";

  const kscEvent = parseKscNotification(body);

  return kscEvent
});

// Code node (Node.js)
// Конвертируем ВСЕ ключи из camelCase в snake_case,
// включая вложенные объекты, если они будут.

function camelToSnake(str) {
  return str.replace(/([A-Z])/g, '_$1').toLowerCase();
}

function transformObject(obj) {
  const out = {};
  for (const [key, value] of Object.entries(obj)) {
    const newKey = camelToSnake(key);
    if (value && typeof value === 'object' && !Array.isArray(value)) {
      out[newKey] = transformObject(value);
    } else {
      out[newKey] = value;
    }
  }
  return out;
}

return $input.all().map(item => {
  return {
    json: transformObject(item.json),
  };
});

const uuid = require('uuid');
const CryptoJS = require('crypto-js');
// если luxon подключён через require, раскомментировать:
// const { DateTime } = require('luxon');

// ===== Константы =====

// Месяцы для парсинга RiseTime
const MONTHS_RU = {
  января: "01", февраля: "02", марта: "03", апреля: "04",
  мая: "05", июня: "06", июля: "07", августа: "08",
  сентября: "09", октября: "10", ноября: "11", декабря: "12",
};

// Маппинг степеней угрозы
const SEVERITY_MAP = {
  "критическое": "Critical",
  "высокое": "High",
  "среднее": "Medium",
  "низкое": "Low",
  "информационное": "Info",

  // сокращённые варианты
  "крит": "Critical",
  "выс": "High",
  "сред": "Medium",
  "низ": "Low",
  "инфо": "Info",
};

// ===== Хелперы =====

// Нормализация severity
function normalizeSeverity(sev) {
  if (!sev) return sev;
  const cleaned = sev.trim().toLowerCase();
  return SEVERITY_MAP[cleaned] || sev;
}

// Парсинг даты RiseTime из формата Kaspersky
function parseKaspeDate(str) {
  if (!str) return null;

  const cleaned = str.replace(" г.", "");

  const match = cleaned.match(
    /(\d{1,2})\s+([А-Яа-я]+)\s+(\d{4})\s+(\d{1,2}:\d{2}:\d{2})\s+\(GMT([+-]\d{2}):?(\d{2})?\)/
    //               ^^^^^ разрешаем 1 или 2 цифры для часа
  );

  if (!match) return null;

  const [, day, monthName, year, time, tzHour, tzMin = "00"] = match;
  const month = MONTHS_RU[monthName.toLowerCase()];
  if (!month) return null;

  const iso = `${year}-${month}-${day.padStart(2, "0")}T${time}${tzHour}:${tzMin}`;
  return DateTime.fromISO(iso);
}

// Генерация устойчивого хэша события
function generateEventHash(item) {
  // безопасно извлекаем rise_time
  let riseTimeStr = "";
  if (item.rise_time) {
    if (typeof item.rise_time === "string") {
      riseTimeStr = item.rise_time;
    } else if (typeof item.rise_time.toString === "function") {
      riseTimeStr = item.rise_time.toString();
    }
  }

  const payload = {
    event: item.event || "",
    computer: item.computer || "",
    ip: item.host_ip || "",
    rise_time: riseTimeStr.split(".")[0] || "",
    raw: item.raw || "",            // логичнее хэшировать raw, чем description/descr
  };

  return CryptoJS.SHA256(JSON.stringify(payload)).toString();
}

// Приведение ключа к snake_case
function toSnakeCaseKey(key) {
  if (!key) return key;

  let cleaned = key.replace(/:$/, "").trim().toLowerCase();

  cleaned = cleaned.replace(/[^\p{L}\p{N}]+/gu, "_");
  cleaned = cleaned.replace(/^_+|_+$/g, "");

  return cleaned;
}

// Парсинг description в JSON-объект со snake_case ключами
function parseDescriptionToJson(description) {
  if (!description || typeof description !== "string") {
    return { raw: description || "" };
  }

  const result = {
    raw: description,
  };

  const lines = description
    .split(/\r?\n/)
    .map((l) => l.trim())
    .filter((l) => l.length > 0);

  for (const line of lines) {
    const match = line.match(/^([^:]+):\s*(.*)$/);
    if (!match) continue;

    const [, rawKey, rawValue] = match;
    const key = toSnakeCaseKey(rawKey);
    const value = rawValue.trim();

    if (!key) continue;

    if (Object.prototype.hasOwnProperty.call(result, key)) {
      if (Array.isArray(result[key])) {
        result[key].push(value);
      } else {
        result[key] = [result[key], value];
      }
    } else {
      result[key] = value;
    }
  }

  return result;
}

// ===== Основной цикл =====

const items = $input.all();

for (const item of items) {
  const json = item.json;

  json.status = "ingested";
  json.updated_at = DateTime.now().toISO();
  json.uuid = uuid.v4();

  // парсим rise_time
  const parsed = parseKaspeDate(json.rise_time);
  json.rise_time = parsed ? parsed.toISO() : json.rise_time || null; 
  // если не смогли распарсить — оставляем исходную строку, чтобы не ломать hash

  // нормализация severity
  json.severity = normalizeSeverity(json.severity);

  // парсим description → JSON
  json.description = parseDescriptionToJson(json.description);

  // добавляем хэш
  json.hash = generateEventHash(json);
}

return items;
